
public class q48 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int year=2000;
		if(year%400==0)
		{
			System.out.println("given number is leap year");
		}
		else if(year%4==0)
		{
			System.out.println("the given number is leap year");
		}
		else
		{
			System.out.println("not leap year");
		}

	}

}
